import React from 'react'
import cloud from '../images/cloud.png'
import {useMediaQuery} from 'react-responsive'

function Cloud (props) {

    const isMobile = useMediaQuery({ query: `(max-width: 1080px)` });

    const nome = props.text.toLowerCase().replaceAll(' ', '-');

    const mobileStyle = {
        position: "relative",
        width: "15%",
        top: "0",
        display: "inline",
        float: "right"
    }

    const cloudStyle = {
        position: "absolute",
        width: "100%",
        top: props.top,
        left: props.left
    };

    const mobileText = {
        fontSize: "75%",
        top: "50%",
        left: "15%"
    };

    function handleClick (e) {
        window.location.href = "#" + e.target.getAttribute('name');
    }

    return (
        <div style={isMobile ? mobileStyle : cloudStyle} onClick={handleClick}>
            <img name={nome} src={cloud} alt="Cloud"/>
            <div style={isMobile && mobileText} name={nome} className="cloud-text">{props.text}</div>
        </div>
    );
}

export default Cloud;
