import React from 'react'
import {useMediaQuery} from 'react-responsive'


function Cloud (props) {

    const isMobile = useMediaQuery({ query: '(max-width: 600px)' });
    const isTablet = useMediaQuery({ query: '(max-width: 1080px)' });

    const nome = props.text.toLowerCase().replaceAll(' ', '-');

    const mobileStyle = {
        position: "relative",
        width: "15%",
        top: "0",
        display: "inline",
        float: "left"
    }

    const cloudStyle = {
        backgroundColor: "rgba(59, 34, 255, 0.3)",
        borderRadius: "100% -25% -25% 100%",
        position: "absolute",
        width: "auto",
        top: props.top,
        left: props.left
    };

    const mobileText = {
        position: "absolute",
        fontSize: isMobile ? "60%" : "80%",
        top: "50%",
        left: "15%",
        fontWeight: "bold",
        color: "#11999E",
        cursor: "pointer"
    };

    const cloudText = {
        position: "relative",
        top: "50%",
        left: "1.5%",
        fontWeight: "bold",
        color: "rgba(255, 255, 255, 0.3)",
        cursor: "pointer"
    }

    function goTo (e) {
        if (e.target.getAttribute('name') !== null) {
            window.location.hash = "#" + e.target.getAttribute('name');
        }
    }

    return (
        <div style={isMobile || isTablet ? mobileStyle : cloudStyle} onClick={goTo}>
            <p style={isMobile || isTablet ? mobileText : cloudText} name={nome}>{props.text}</p>
        </div>
    );
}

export default Cloud;
